var g = require('./index');

console.log(g.id());              // 1 (consecutive calls return 2, 3, 4, ..)
console.log(g.random(20));        // 13
console.log(g.name());            // 'John', 'Olivia`
console.log(g.address.city());    // 'Mexico City', 'Beijing'
console.log(g.address.zipUS());   // '53142', '71238'
console.log(g.address.zipNL());   // '4715 FW', '7551 VT'
console.log(g.time.byQuarter());  // '14:45', '9:00'

console.log(g.image.base64({width:200, height: 200}));

console.log(g.image.base64({path:'/200x300&text=dummyimage.com+rocks!'}));

console.log(g.image.base64({
    host: 'http://lorempixel.com',
    path: '/150/150/abstract/' + g.random(10)
}).then(function(res) {
        console.log(res);
    }));
